import React, {Component} from 'react';
import { Link } from 'react-router-dom';
import 'antd/dist/antd.css';
import './LoginForm.css';
import { Form, Icon, Input, Button, Checkbox, Radio } from 'antd';

class LoginForm extends React.Component {
    //用户种类初始状态
    state = {
        value: 1,
    };

    //用户种类改变
    onChange = e => {
        console.log('radio checked', e.target.value);
        this.setState({
            value: e.target.value,
        });
    };

    //登录按钮提交
    handleSubmit = e => {
        e.preventDefault();
        let userInfo = this.props.form.getFieldsValue();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);
                //如果没有错误，则发送用户信息给后端
            }
        });
    };

    render() {
        const { getFieldDecorator } = this.props.form;
        return (

            <Form onSubmit={this.handleSubmit} className="login-form">
                <p className={"login"}>Login in</p>
                <Form.Item>
                    {getFieldDecorator('username', {
                        initialValue:'',
                        rules: [
                            {
                                required: true,
                                message: 'Please input your username!'
                            },
                            {
                                min:5,max:10,
                                message:'用户名长度不在范围内'
                            },
                            {
                                pattern:new RegExp('^\\w+$','g'),
                                message:'用户名必须为字母或者数字'
                            }
                        ],
                    })(
                        <Input
                            prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                            type="text"
                            placeholder="请输入用户名"/>,
                    )}
                </Form.Item>
                <Form.Item>
                    {getFieldDecorator('password', {
                        initialValue:'',
                        rules: [
                            {
                                required: true,
                                message: 'Please input your Password!'
                            }
                        ],
                    })(
                        <Input
                            prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />}
                            type="password"
                            placeholder="请输入密码"/>,
                    )}
                </Form.Item>
                <Form.Item>
                    {getFieldDecorator('root', {
                        initialValue:1,


                    })(<Radio.Group className={"idType"}>
                        <Radio value={1}>投保人</Radio>
                        <Radio value={2}>保险人</Radio>
                        <Radio value={3}>维修商</Radio>
                    </Radio.Group>)}


                    {getFieldDecorator('remember', {
                        valuePropName: 'checked',
                        initialValue: true,
                    })(<Checkbox className={"check"}>Remember me</Checkbox>)}

                    <Button type="primary" htmlType="submit" className="login-form-button">
                        Log in
                    </Button>
                    <span className={"register"}> Or <Link to="/Register">register now!</Link></span>
                </Form.Item>
            </Form>
        );
    }
}


export default Form.create()(LoginForm);
