import React, { Component } from 'react';
import {Form, Input, Button, Radio, Icon} from 'antd';
import { Row, Col } from 'antd';
import { Select } from 'antd';

const { Option } = Select;


class ApplyForm extends React.Component {
    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);
            }
        });
    };

    render() {
        const { getFieldDecorator } = this.props.form;

        return (

            <Form onSubmit={this.handleSubmit}
                  layout={'hori'}
                  className={'applyForm'}
                >

                <Row>
                    <Col span={12}>
                    <Form.Item label="姓名">
                        {getFieldDecorator('name', {
                            rules: [{ required: true, message: '请输入您的姓名' }],
                        })(
                            <Input
                                prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                                style={{width:260}}
                                placeholder="请输入您的姓名"
                            />,
                        )}
                    </Form.Item>
                    </Col>

                    <Col span={12}>
                    <Form.Item label="电话">
                        {getFieldDecorator('phone', {
                            rules: [{ required: true, message: '请输入您的手机号' }],
                        })(
                            <Input
                                prefix={<Icon type="phone" style={{ color: 'rgba(0,0,0,.25)' }} />}
                                type="text"
                                style={{width:260}}
                                placeholder="请输入您的手机号"
                            />,
                        )}
                    </Form.Item>
                    </Col>
                </Row>

                <Row>
                    <Col span={12}>
                        <Form.Item label="性别">
                            {getFieldDecorator('gender', {
                                initialValue:1,
                                rules: [{ required: true, message: '请选择您的性别' }],
                            })(<Radio.Group className={"gender"}>
                                <Radio value={1}>男</Radio>
                                <Radio value={0}>女</Radio>
                            </Radio.Group>)}
                        </Form.Item>
                    </Col>

                    <Col span={12}>
                        <Form.Item label="保险单号">
                            {getFieldDecorator('insureNum', {
                                rules: [{ required: true, message: '请输入您的保险单号' }],
                            })(
                                <Input
                                    prefix={<Icon type="key" style={{ color: 'rgba(0,0,0,.25)' }} />}
                                    style={{width:260}}
                                    placeholder="请输入您的保险单号"
                                />,
                            )}
                        </Form.Item>
                    </Col>
                </Row>

                <Row>
                    <Col span={12}>
                        <Form.Item label="身份证">
                            {getFieldDecorator('idNum', {
                                rules: [{ required: true, message: '请输入您的身份证' }],
                            })(
                                <Input
                                    prefix={<Icon type="idcard" style={{ color: 'rgba(0,0,0,.25)' }} />}
                                    style={{width:260}}
                                    placeholder="请输入您的身份证"
                                />,
                            )}
                        </Form.Item>
                    </Col>

                    <Col span={12}>
                        <Form.Item label="车险类型">
                            {getFieldDecorator('carInsure', {
                                rules: [{ required: true, message: '请选择您购买的车险类型' }],
                            })(
                                <Select placeholder="请选择您购买的车险类型">
                                    <Option value="trafficInsure">交强险</Option>
                                    <Option value="damageInsure">车辆损失险</Option>
                                    <Option value="thirdInsure">第三者责任险</Option>
                                    <Option value="thirdInsure">第三者责任险</Option>
                                    <Option value="stoleInsure">全车盗抢险</Option>
                                    <Option value="deviceInsure">设备损失险</Option>
                                </Select>,
                            )}
                        </Form.Item>
                    </Col>
                </Row>

                <Row>
                    <Col span={12}>
                        <Form.Item label="理赔金额">
                            {getFieldDecorator('count', {
                                rules: [{ required: true, message: '请输入您的理赔金额（元）' }],
                            })(
                                <Input
                                    prefix={<Icon type="pay-circle" style={{ color: 'rgba(0,0,0,.25)' }} />}
                                    style={{width:260}}

                                    placeholder="请填写您的申请理赔金额"
                                />,
                            )}
                        </Form.Item>
                    </Col>

                    <Col span={12}>
                        <Form.Item label="住址">
                            {getFieldDecorator('location', {
                                rules: [{ required: true, message: '请输入您的住址' }],
                            })(
                                <Input
                                    prefix={<Icon type="bank" style={{ color: 'rgba(0,0,0,.25)' }} />}
                                    type="text"
                                    style={{width:260}}
                                    placeholder="请输入您的住址"
                                />,
                            )}
                        </Form.Item>
                    </Col>
                </Row>




                <Form.Item style={{width:100}}>
                    <Button type="primary" htmlType="submit" className="login-form-button">
                        Log in
                    </Button>
                </Form.Item>
            </Form>
        );
    }
}

export default Form.create()(ApplyForm);
