import React from 'react';
import 'antd/dist/antd.css';
import {Menu, Icon, Layout} from 'antd';
import { Link } from 'react-router-dom';


class Menus extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            current:"1"
        };
    }

    onClick = e => {
        this.setState({
            selectedKeys:e.key
        })
    };
    // componentDidMount = () =>{
    //     let defaultRoute = this.props.location.pathname;
    //     this.setState({
    //         current:defaultRoute.subString(defaultRoute.lastIndexOf("/")+1,
    //             defaultRoute.length)
    //     });
    //     history.listen((event)=>{
    //         let test = event.pathname;
    //         let text = test.subString(test.lastIndexOf("/")+1,
    //             test.length);
    //         this.setState({
    //             current:text
    //         })
    //     })
    // };


  render(){
      return(
          <Menu
              mode="inline"
              defaultSelectedKeys={['1']}
              style={{ height: '100%' }}
              onClick={this.onClick.bind(this)}
              selectedKeys={[this.state.selectedKeys]}
              >

              <Menu.Item
                  key="Problem"
                  style={{height:50,fontSize:15}}>
                  <Link to="/Problem"><Icon type="question-circle" />常见问题</Link>
              </Menu.Item>

              <Menu.Item
                  key="Damage"
                  style={{height:50,fontSize:15}}>
                  <Link to="/Damage"><Icon type="tool" />受损状况</Link>
              </Menu.Item>

              <Menu.Item
                  key="Apply"
                  style={{height:50,fontSize:15}}>
                 <Link to="/Apply"><Icon type="snippets" />申请理赔</Link>}
              </Menu.Item>

              <Menu.Item key="Progress" style={{height:50,fontSize:15}}>
                <Link to="/Progress"><Icon type="hourglass" />理赔进度</Link>
              </Menu.Item>

              <Menu.Item key="History" style={{height:50,fontSize:15}}>
              <Link to="/History"><Icon type="history" />历史理赔</Link>
              </Menu.Item>

          </Menu>

      )
  }

}

export default Menus;
