import React from 'react';
import 'antd/dist/antd.css';
import {Menu, Icon, Layout} from 'antd';
import { Link } from 'react-router-dom';

global.sidemenus = [
    {
        key: '1',
        title: '常见问题',
        icon: 'question-circle',
        route: '/Problem'
    },
    {
        key: '2',
        title: '受损状况',
        icon: 'tool',
        route: '/Damage'
    },
    {
        key: '3',
        title: '申请理赔',
        icon: 'snippets',
        route: '/Apply'
    },
    {
        key: '4',
        title: '理赔进度',
        icon: 'hourglass',
        route: '/Progress'
    },
    {
        key: '5',
        title: '历史理赔',
        icon: 'history',
        route: '/History'
    },
];

class Menus extends React.Component {
  handleClick = e => {
    console.log('click ', e);
  };



  renderMenuItem = ({key, icon, title, route}) => {
    return (
        <Menu.Item key={key} style={{height:50,fontSize:15}}>
            <Link to={key}><Icon type={icon}/>{title}</Link>
        </Menu.Item>

    );
  }

  render(){
      return(
          <Menu
            mode="inline"
            defaultSelectedKeys={['1']}
            style={{ height: '100%' }}
            onClick={this.handleClick}
          >
              {
                  sidemenus.map(item => {
                      this.renderMenuItem(item)
                  })
              }
          </Menu>

      )
  }

}

export default Menus;
