import React, { Component } from 'react';
import {Form, Input, Button, Radio, Icon} from 'antd';
import {Link} from "react-router-dom";
import Menus from "../Menus";

class ApplyForm extends Component {

    constructor(props){
        super(props);
        this.state = {
            registerFlag:false,
            confirmDirty: false,
            autoCompleteResult: [],
        };
    }

    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
            }
        });
    };



    render() {
        const { getFieldDecorator } = this.props.form;
        const formItemLayout = {
            labelCol: {
                xs: { span: 24 },
                sm: { span: 5 },
            },
            wrapperCol: {
                xs: { span: 24 },
                sm: { span: 16 },
            },
        };
        const tailFormItemLayout = {
            wrapperCol: {
                xs: {
                    span: 24,
                    offset: 0,
                },
                sm: {
                    span: 16,
                    offset: 8,
                },
            },
        };

        return (
            <Form {...formItemLayout}
                  onSubmit={this.handleSubmit}
                  className="register-form"
                  layout={"inline"}>
                <h2 className={"Register"}>WELCOME TO JOIN US</h2>
                <Form.Item label="用户名">
                    {getFieldDecorator('usr', {
                        rules: [
                            {
                                required: true,
                                message: '请输入您的用户名'
                            },
                            {
                                min:5,max:10,
                                message:'用户名为5-10位字符'
                            },
                            {
                                pattern:new RegExp('^\\w{5,10}$','g'),
                                message:'用户名必须为字母或者数字'
                            }
                        ],
                    })(
                        <Input type="text" placeholder="请输入用户名"/>,
                    )}
                </Form.Item>

                <Form.Item label="邮箱">
                    {getFieldDecorator('email', {
                        rules: [
                            {
                                type: 'email',
                                message: '邮箱格式不正确',
                            },
                            {
                                required: true,
                                message: '请输入您的邮箱',
                            },
                        ],
                    })(<Input placeholder={"请输入您的邮箱"}/>)}
                </Form.Item>


                <Form.Item label="真实姓名">
                    {getFieldDecorator('realname', {
                        rules: [
                            {
                                required: true,
                                message: '请输入您的真实姓名'
                            },
                            {
                                pattern:new RegExp('^[\u4e00-\u9fa5]+$','g'),
                                message:'真实姓名有误'
                            }
                        ],
                    })(<Input placeholder={"请输入您的真实姓名"}/>)}
                </Form.Item>


                <Form.Item label="手机号">
                    {getFieldDecorator('phone', {
                        rules: [
                            {
                                required: true,
                                message: '请输入您的手机号'
                            },
                            {
                                pattern:new RegExp('^1[3456789]\\d{9}$','g'),
                                message:'用户名必须为字母或者数字'
                            }
                        ],
                    })(<Input placeholder={"请输入您的手机号"}/>)}
                </Form.Item>


                <Form.Item {...tailFormItemLayout}>
                    <Button type="primary" htmlType="submit">
                        Register
                    </Button>
                </Form.Item>
            </Form>
            );
        }
    }

export default ApplyForm;
