import React, {Component} from 'react';
import { Link } from 'react-router-dom';
import 'antd/dist/antd.css';
import './LoginForm.css';
import { Form, Icon, Input, Button, Checkbox, Radio } from 'antd';

class LoginForm extends React.Component {
    //用户种类初始状态
    state = {
        value: 1,
    };

    //用户名、密码、记住用户状态初始化
    constructor(props){
        super(props);
        this.state = {
            userName: "",
            userPassword: "",
            isRemember:false,
            unameHelp:"",
            upwdHelp:""
        };
    }

    //用户名框
    changeUsername = e => {
        let uname = e.target.value;
        this.setState({
            userName:uname
        });
        console.log(this.state.userName);
    };

    //密码框
    changePassword = e => {
        let upwd = e.target.value;
        this.setState({
            userPassword: upwd
        })
    };

    //用户种类改变
    onChange = e => {
        console.log('radio checked', e.target.value);
        this.setState({
            value: e.target.value,
        });
    };

    //记住用户选项
    handleCheck = e => {
        let isChecked = e.target.checked;
        if(isChecked) {
            this.setState({
                isRemember: true
            })
        }
        else{
            this.setState({
                isRemember: false
            })
        }
    };



    //登录按钮提交
    handleSubmit = e => {
        let userInfo = this.props.form.getFieldsValue();
        this.props.form.validateFields((err, values) => {
            if (!err) {

                console.log('Received values of form: ', values);
                //如果没有错误，则发送用户信息给后端
            }
        });
    };

    render() {
        const { getFieldDecorator } = this.props.form;
        return (

            <Form onSubmit={this.handleSubmit} className="login-form">
                <p className={"login"}>Login in</p>
                <Form.Item>
                    {getFieldDecorator('username', {
                        rules: [{ required: true, message: 'Please input your username!' }],
                    })(
                        <Input
                            prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                            placeholder="Username"
                            name={"Username"}
                            id={"uname"}
                            ref={"uname"}
                            onChange={this.changeUsername.bind(this)}
                        />,
                    )}
                </Form.Item>
                <Form.Item>
                    {getFieldDecorator('password', {
                        rules: [{ required: true, message: 'Please input your Password!' }],
                    })(
                        <Input
                            prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />}
                            type="password"
                            placeholder="Password"
                            name={"Password"}
                            id={"upwd"}
                            ref={"upwd"}
                            onChange={this.changePassword.bind(this)}
                        />,
                    )}
                </Form.Item>
                <Form.Item>
                    <Radio.Group className={"idType"} onChange={this.onChange} value={this.state.value}>
                        <Radio value={1}>投保人</Radio>
                        <Radio value={2}>保险人</Radio>
                        <Radio value={3}>维修商</Radio>
                    </Radio.Group>
                    {getFieldDecorator('remember', {
                        valuePropName: 'checked',
                        initialValue: true,
                    })(
                        <Checkbox
                            className={"check"}
                            id={"chk"}
                            checked={this.state.isRemember}
                            onClick={this.handleCheck.bind(this)}
                        >Remember me</Checkbox>)}

                    <Button type="primary" htmlType="submit" className="login-form-button">
                        Log in
                    </Button>
                    <span className={"register"}> Or <Link to="/Register">register now!</Link></span>
                </Form.Item>
            </Form>
        );
    }
}


export default Form.create()(LoginForm);
