import React, { Component } from 'react';
import {Form, Input, Button, Radio, Icon} from 'antd';
import { Row, Col } from 'antd';
import { DatePicker, TimePicker } from 'antd';
import {Link} from "react-router-dom";


class ApplyForm extends React.Component {
    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);
            }
        });
    };

    render() {
        const { getFieldDecorator } = this.props.form;
        const config = {
            rules: [{ type: 'object', required: true, message: '请选择出生日期' }],
        };

        return (

            <Form onSubmit={this.handleSubmit}
                  layout={'hori'}
                  className={'applyForm'}
                >

                <Row>
                    <Col span={12}>
                    <Form.Item label="姓名">
                        {getFieldDecorator('name', {
                            rules: [{ required: true, message: '请输入您的姓名' }],
                        })(
                            <Input
                                prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                                style={{width:260}}
                                placeholder="请输入您的姓名"
                            />,
                        )}
                    </Form.Item>
                    </Col>

                    <Col span={12}>
                    <Form.Item label="电话">
                        {getFieldDecorator('phone', {
                            rules: [{ required: true, message: '请输入您的手机号' }],
                        })(
                            <Input
                                prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />}
                                type="text"
                                style={{width:260}}
                                placeholder="请输入您的手机号"
                            />,
                        )}
                    </Form.Item>
                    </Col>
                </Row>

                <Row>
                    <Col span={12}>
                        <Form.Item label="性别">
                            {getFieldDecorator('gender', {
                                initialValue:1,
                                rules: [{ required: true, message: '请选择您的性别' }],
                            })(<Radio.Group className={"gender"}>
                                <Radio value={1}>男</Radio>
                                <Radio value={0}>女</Radio>
                            </Radio.Group>)}
                        </Form.Item>
                    </Col>

                    <Col span={12}>
                        <Form.Item label="DatePicker">
                            {getFieldDecorator('date-picker', config)(<DatePicker />)}
                        </Form.Item>
                    </Col>
                </Row>

                <Row>
                    <Col span={12}>
                        <Form.Item label="身份证">
                            {getFieldDecorator('idNum', {
                                rules: [{ required: true, message: '请输入您的身份证' }],
                            })(
                                <Input
                                    prefix={<Icon type="card" style={{ color: 'rgba(0,0,0,.25)' }} />}
                                    style={{width:260}}
                                    placeholder="请输入您的身份证"
                                />,
                            )}
                        </Form.Item>
                    </Col>

                    <Col span={12}>
                        <Form.Item label="车险类型">
                            {getFieldDecorator('carType', {
                                rules: [{ required: true, message: '请选择您购买的车险类型' }],
                            })(
                                <Input
                                    prefix={<Icon type="car" style={{ color: 'rgba(0,0,0,.25)' }} />}
                                    type="text"
                                    style={{width:260}}
                                    placeholder="请输入您的手机号"
                                />,
                            )}
                        </Form.Item>
                    </Col>
                </Row>

                <Row>
                    <Col span={12}>
                        <Form.Item label="姓名">
                            {getFieldDecorator('name', {
                                rules: [{ required: true, message: '请输入您的姓名' }],
                            })(
                                <Input
                                    prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                                    style={{width:260}}

                                    placeholder="请输入您的姓名"
                                />,
                            )}
                        </Form.Item>
                    </Col>

                    <Col span={12}>
                        <Form.Item label="电话">
                            {getFieldDecorator('phone', {
                                rules: [{ required: true, message: '请输入您的手机号' }],
                            })(
                                <Input
                                    prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />}
                                    type="text"
                                    style={{width:260}}
                                    placeholder="请输入您的手机号"
                                />,
                            )}
                        </Form.Item>
                    </Col>
                </Row>




                <Form.Item style={{width:100}}>
                    <Button type="primary" htmlType="submit" className="login-form-button">
                        Log in
                    </Button>
                </Form.Item>
            </Form>
        );
    }
}

export default Form.create()(ApplyForm);
