import React, {Component} from 'react';
import { Link } from 'react-router-dom';
import 'antd/dist/antd.css';
import './LoginForm.css';
import { Form, Icon, Input, Button, Checkbox, Radio } from 'antd';

class LoginForm extends React.Component {
    state = {
        value: 1,
    };

    onChange = e => {
        console.log('radio checked', e.target.value);
        this.setState({
            value: e.target.value,
        });
    };

    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);
            }
        });
    };

    render() {
        const { getFieldDecorator } = this.props.form;
        return (

            <Form onSubmit={this.handleSubmit} className="login-form">
                <p className={"login"}>Login in</p>
                <Form.Item>
                    {getFieldDecorator('username', {
                        rules: [{ required: true, message: 'Please input your username!' }],
                    })(
                        <Input
                            prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                            placeholder="Username"
                        />,
                    )}
                </Form.Item>
                <Form.Item>
                    {getFieldDecorator('password', {
                        rules: [{ required: true, message: 'Please input your Password!' }],
                    })(
                        <Input
                            prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />}
                            type="password"
                            placeholder="Password"
                        />,
                    )}
                </Form.Item>
                <Form.Item>
                    <Radio.Group className={"idType"} onChange={this.onChange} value={this.state.value}>
                        <Radio value={1}>投保人</Radio>
                        <Radio value={2}>保险人</Radio>
                        <Radio value={3}>维修商</Radio>
                    </Radio.Group>
                    {getFieldDecorator('remember', {
                        valuePropName: 'checked',
                        initialValue: true,
                    })(<Checkbox className={"check"}>Remember me</Checkbox>)}

                    <Button type="primary" htmlType="submit" className="login-form-button">
                        Log in
                    </Button>
                    <span className={"register"}> Or <Link to="/Register">register now!</Link></span>
                </Form.Item>
            </Form>
        );
    }
}


export default Form.create()(LoginForm);
